import React from 'react';

const Conference = () => {
  return (
    <div className="container mt-5">
      <h2>Conference Room</h2>
      <p>
        The Conference Room offers a professional environment with modern facilities for meetings, presentations, and online conferences.
      </p>
    </div>
  );
};

export default Conference;
